const POLL_NAME = "AP Top 25";
const TOP_N = 25;

try {
    // If HTTP Request still returns text, parse it.
    // (This makes the flow robust even if someone forgets to set ret=obj)
    if (typeof msg.payload === "string") {
        msg.payload = JSON.parse(msg.payload);
    }

    const root = msg.payload;

    if (!Array.isArray(root) || root.length < 1) {
        throw new Error("Expected msg.payload to be a non-empty array.");
    }

    const data = root[0];
    const polls = data && data.polls;

    if (!Array.isArray(polls) || polls.length === 0) {
        throw new Error("Expected msg.payload[0].polls to be a non-empty array.");
    }

    const pollObj = polls.find(p => p && p.poll === POLL_NAME) || polls[0];
    const ranks = pollObj && pollObj.ranks;

    if (!Array.isArray(ranks) || ranks.length === 0) {
        throw new Error(`No ranks found for poll: ${pollObj?.poll ?? "(unknown)"}`);
    }

    const top = ranks.slice(0, TOP_N).map(r => ({
        Rank: r.rank ?? null,
        School: r.school ?? "",
        Conference: r.conference ?? "",
        Points: r.points ?? 0,
        FirstPlaceVotes: r.firstPlaceVotes ?? 0
    }));

    msg.payload = top;
    msg.meta = {
        season: data.season,
        seasonType: data.seasonType,
        week: data.week,
        poll: pollObj.poll
    };

    return msg;

} catch (err) {
    msg.payload = [{ Rank: "-", School: "ERROR", Conference: err.message }];
    return msg;
}
